package homework_basic;

public class _01_basic_6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 6. 1 ~ 100까지 합을 while문으로 출력
				System.out.println("6번");
				
				int l = 1;
				int o = 0;
				
				while(l <= 100) {
					o += l++;
					
					
				}
					System.out.println(+ o);
	}
}
